import React from 'react';
import { Route } from 'react-router-dom';
import { connect } from 'react-redux';

import CollectionsOverview from '../../components/collections-overview/collections-overview.component';
import CollectionPage from '../collection/collection.component';
import WithSpinner from '../../components/with-spinner/with-spinner.component';

import { fetchCollectionsAsyncStart } from '../../redux/shop/shop.actions';

const CollectionsOverviewWithSpinner = WithSpinner(CollectionsOverview);
const CollectionPageWithSpinner = WithSpinner(CollectionPage);

class ShopPage extends React.Component {
  constructor() {
    super();
    
    this.state = {
      isLoading: true
    };
  }

  unsubscribeFromSnapshot = null;

  componentDidMount() {
    fetchCollectionsAsyncStart();
  }

  render() {
    const { match, isFetching } = this.props
    return (
      <div className='shop-page'>
        <Route exact path={`${match.path}`}
          render={(props) => <CollectionsOverviewWithSpinner isLoading={isFetching} {...props} />}
        />
        <Route path={`${match.path}/:collectionId`}
          render={(props) => <CollectionPageWithSpinner isLoading={isFetching} {...props} />}
        />
      </div>
    );
  }
}

function mapStateToProps(state) {
  return {
    isFetching: state.shop.isFetching
  };
}

function mapDispatchToProps(dispatch) {
  return {
    fetchCollectionsAsyncStart: () => dispatch(fetchCollectionsAsyncStart())
  };
}

export default connect(mapStateToProps, mapDispatchToProps)(ShopPage);
