import { takeLatest, put } from 'redux-saga/effects';

import userActionTypes from './user.types';
import { googleSigninSuccess, googleSigninFailure} from './user.action';

import { auth, googleProvider, createUserProfileDocument } from '../../firebase/firebaseutils';

export function* googleSigninStart() {
  yield takeLatest(userActionTypes.GOOGLE_SIGNIN_START, signInWithGoogle);
}

export function* signInWithGoogle() {
  try {
    const { user: userAuth } = yield auth.signInWithPopup(googleProvider);
    const userRef = yield createUserProfileDocument(userAuth);
    const userSnapshot = yield userRef.get();
    yield put( googleSigninSuccess({ id: userSnapshot.id, ...userSnapshot.data()}) );
  }
  catch (error) {
    yield put( googleSigninFailure(error) );
  }
}

export function* emailSigninStart() {
  yield takeLatest(userActionTypes.EMAIL_SIGNIN_START);
}

export function* signInWithEmail({email, password}) {
  yield auth.signInWithEmailAndPassword(email, password);
  
}
