import { takeEvery } from 'redux-saga/effects';

import { firestore, convertCollectionSnapshotToMap } from '../../firebase/firebaseutils';

import ShopActionTypes from './shop.types';
import { fetchCollectionsSuccess, fetchColletionsFailiure } from './shop.actions';

export function* fetchCollectionAsync() {
  yield console.log('Fired!!');

  const colletctionRef = firestore.collection('collection');
  const snapshot = yield collectionRef.get();
  const collections = yield call(convertCollectionSnapshotToMap(snapshot))
  
  colletctionRef.get().then(snapshot => {
    dispatch(fetchCollectionsSuccess(collections));
  }).catch(error => {
    dispatch(fetchCollectionsFailure(error.message));
  })
}

export function* fetchCollectionStart() {
  yield takeEvery(
    ShopActionTypes.FETCH_COLLECTIONS_START,
    fetchCollectionAsync
  );
}
