import { createSelector } from 'reselect';

export const COLLECTION_ID_MAP = {
  hats: 1,
  sneakers: 2,
  jackets: 3,
  womens: 4,
  mens: 5,
}

function selectShop(state) {
  return state.shop;
}

export const selectCollections = createSelector(
  [selectShop],
  shop => shop.collections
