import { takeLatest, put } from 'redux-saga/effects';

import userActionTypes from './user.types';
import { googleSigninStart, googleSigninFailure } from './user.action';

import { auth, googleProvider, createUserProfileDocument } from '../../firebase/firebaseutils';

export function* signInWithGoogle() {
  try {
    const { userAuth } = yield auth.signInWithPopup(googleProvider);
    const userRef = yield createUserProfileDocument(userAuth);
    const userSnapshot = yield userRef.get();
    
  }
  catch (error) {
    console.log('error');
  }
}

export function* onGoogleSigninStart() {
  yield takeLatest(userActionTypes.GOOGLE_SIGNIN_START, signInWithGoogle);
}
