import { takeLatest, put } from 'redux-saga/effects';

import userActionTypes from './user.types';

import { auth, googleProvider } from '../../firebase/firebaseutils';

export function* signInWithGoogle() {
  try {
    const whatsReturned = yield auth.signInWithPopup(googleProvider);
    console.log(whatsReturned);
  }
  catch (error) {
    
  }
}

export function* googleSigninStart() {
  takeLatest(userActionTypes.GOOGLE_SIGNIN_START, signInWithGoogle)
}
