import React from 'react';
import { Switch, Route, Redirect } from 'react-router-dom';
import { connect } from 'react-redux';

import './App.css';

import HomePage from './pages/homepage/homepage.component';
import ShopPage from './pages/shop/shop.component';
import CheckoutPage from './pages/checkout/checkout.component';
import Header from './components/header/header-component';
import SignInAndSignUpPage from './pages/signIn-and-signUp/signIn-and-signUp';
import { auth, createUserProfileDocument, addCollectionAndDocuments } from './firebase/firebaseutils';
import { setCurrentUser } from './redux/user/user.action';
import { selectCurrentUser } from './redux/user/user.selector';
import { selectCollections } from './redux/shop/shop.selectors';

class App extends React.Component {
  unsubscribeFromAuth = null;

  componentDidMount() {
    const { setCurrentUser, collectionsArray } = this.props;

    this.unsubscribeFromAuth = auth.onAuthStateChanged(async userAuth => {
      if (!userAuth) {
        setCurrentUser(null);
        return;
      };

      const userRef = await createUserProfileDocument(userAuth);
      userRef.onSnapshot(snapshot => {
        let currentUser = { id: snapshot.id, ...snapshot.data() };
        setCurrentUser(currentUser);
      });
    });

    addCollectionAndDocuments('collection', collectionsArray);
  }

  componentWillUnmount() {
    this.unsubscribeFromAuth();
  }

  render() {
    return (
      <div className="App">
        <Header />
        <Switch>
          <Route exact path='/' component={HomePage} />
          <Route path='/shop' component={ShopPage} />
          <Route exact path='/checkout' component={CheckoutPage} />
          <Route
            exact
            path='/signin'
            render={() =>
              this.props.currentUser ? (
                <Redirect to='/' />
              ) : (
                <SignInAndSignUpPage />
              )
            }
          />
        </Switch>
      </div>
    );
  }
}

function mapStateToProps(state) {
  return {
    currentUser: selectCurrentUser(state),
    collectionsArray: Object.values( selectCollections(state) ),
  };
}

function mapDispatchToProps(dispatch) {
  return {
    setCurrentUser: user => dispatch( setCurrentUser(user) )
  }
}

export default connect(mapStateToProps, mapDispatchToProps)(App);
